package com.hynet.heebit.components.widget.badge;import android.graphics.Canvas;import android.graphics.Paint;import java.util.Random;public class BitmapFragment {    private Random random;    private float x;    private float y;    private float size;    private int color;    private int maxSize;    private Paint paint;    public Random getRandom() {        return random;    }    public void setRandom(Random random) {        this.random = random;    }    public float getX() {        return x;    }    public void setX(float x) {        this.x = x;    }    public float getY() {        return y;    }    public void setY(float y) {        this.y = y;    }    public float getSize() {        return size;    }    public void setSize(float size) {        this.size = size;    }    public int getColor() {        return color;    }    public void setColor(int color) {        this.color = color;    }    public int getMaxSize() {        return maxSize;    }    public void setMaxSize(int maxSize) {        this.maxSize = maxSize;    }    public Paint getPaint() {        return paint;    }    public void setPaint(Paint paint) {        this.paint = paint;    }    public BitmapFragment() {        this.paint = new Paint();        this.paint.setAntiAlias(true);        this.paint.setStyle(Paint.Style.FILL);        this.random = new Random();    }    public void updata(float value, Canvas canvas) {        paint.setColor(color);        this.x = x + 0.1f * random.nextInt(maxSize) * (random.nextFloat() - 0.5f);        this.y = y + 0.1f * random.nextInt(maxSize) * (random.nextFloat() - 0.5f);        canvas.drawCircle(x, y, size - value * size, paint);    }}