package com.hynet.heebit.components.widget.chart.renderer.info;import android.graphics.PointF;public class PlotArcLabelInfo extends PlotDataInfo {    public float radius = 0.0f;    public float offsetAngle = 0.0f;    public float currentAngle = 0.0f;    private PointF labelPointF = null;    public PlotArcLabelInfo() {}    public PlotArcLabelInfo(int id, float x, float y, float radius, float offsetAngle, float currentAngle) {        ID = id;        X = x;        Y = y;        this.radius = radius;        this.offsetAngle = offsetAngle;        this.currentAngle = currentAngle;    }    public float getRadius() {        return radius;    }    public void setRadius(float radius) {        this.radius = radius;    }    public float getOffsetAngle() {        return offsetAngle;    }    public void setOffsetAngle(float offsetAngle) {        this.offsetAngle = offsetAngle;    }    public float getCurrentAngle() {        return currentAngle;    }    public void setCurrentAngle(float currentAngle) {        this.currentAngle = currentAngle;    }    public PointF getLabelPointF() {        return labelPointF;    }    public void setLabelPointF(PointF point) {        this.labelPointF = point;    }}