package com.hynet.heebit.components.widget.spinner.adapter;import android.content.Context;import android.widget.ListAdapter;import com.hynet.heebit.components.widget.spinner.listener.OnTextFormatListener;public class TextViewSpinnerAdapterWrapper extends BaseAdapter {    private final ListAdapter baseAdapter;    public TextViewSpinnerAdapterWrapper(Context context, ListAdapter listAdapter, int textColor, int backgroundSelector, OnTextFormatListener onTextFormatListener) {        super(context, textColor, backgroundSelector, onTextFormatListener);        this.baseAdapter = listAdapter;    }    @Override public int getCount() {        return baseAdapter.getCount() - 1;    }    @Override public Object getItem(int position) {        return baseAdapter.getItem(position >= selectedIndex ? position + 1 : position);    }    @Override public Object getItemInDataset(int position) {        return baseAdapter.getItem(position);    }}