package com.hynet.heebit.components.widget.tablayout.listener.implement;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import com.hynet.heebit.components.utils.ViewUtil;import com.hynet.heebit.components.widget.tablayout.listener.TabProvider;import androidx.viewpager.widget.PagerAdapter;public class SimpleTabProvider implements TabProvider {    private final Context context;    private final int tabViewLayoutId;    private final int tabViewTextViewId;    public SimpleTabProvider(Context context, int layoutResId, int textViewId) {        this.context = context;        this.tabViewLayoutId = layoutResId;        this.tabViewTextViewId = textViewId;    }    @Override    public View createTabView(ViewGroup container, int position, PagerAdapter pagerAdapter) {        View tabView = null;        TextView tabTitleView = null;        if (tabViewLayoutId != View.NO_ID) {            tabView = LayoutInflater.from(context).inflate(tabViewLayoutId, container, false);        }        if (tabViewTextViewId != View.NO_ID && tabView != null) {            tabTitleView = ViewUtil.Companion.getInstance().findView(tabView, tabViewTextViewId);        }        if (tabTitleView == null && TextView.class.isInstance(tabView)) {            tabTitleView = (TextView) tabView;        }        if (tabTitleView != null) {            tabTitleView.setText(pagerAdapter.getPageTitle(position));        }        return tabView;    }}