package com.hynet.heebit.components.widget.tablayout.v4;import android.view.ViewGroup;import java.lang.ref.WeakReference;import androidx.collection.SparseArrayCompat;import androidx.fragment.app.Fragment;import androidx.fragment.app.FragmentManager;import androidx.fragment.app.FragmentStatePagerAdapter;public class FragmentStatePagerItemAdapter extends FragmentStatePagerAdapter {    private final FragmentPagerItems fragmentPagerItems;    private final SparseArrayCompat<WeakReference<Fragment>> holder;    public FragmentStatePagerItemAdapter(FragmentManager fragmentManager, FragmentPagerItems fragmentPagerItems) {        super(fragmentManager);        this.fragmentPagerItems = fragmentPagerItems;        this.holder = new SparseArrayCompat<>(fragmentPagerItems.size());    }    @Override    public int getCount() {        return fragmentPagerItems.size();    }    @Override    public Fragment getItem(int position) {        return getPagerItem(position).instantiate(fragmentPagerItems.getContext(), position);    }    @Override    public Object instantiateItem(ViewGroup container, int position) {        Object item = super.instantiateItem(container, position);        if (item instanceof Fragment) {            holder.put(position, new WeakReference<>((Fragment) item));        }        return item;    }    @Override    public void destroyItem(ViewGroup container, int position, Object object) {        holder.remove(position);        super.destroyItem(container, position, object);    }    @Override    public CharSequence getPageTitle(int position) {        return getPagerItem(position).getTitle();    }    @Override    public float getPageWidth(int position) {        return getPagerItem(position).getWidth();    }    public Fragment getPage(int position) {        final WeakReference<Fragment> weakRefItem = holder.get(position);        return (weakRefItem != null) ? weakRefItem.get() : null;    }    protected FragmentPagerItem getPagerItem(int position) {        return fragmentPagerItems.get(position);    }}